mapper.select:
select ti.id, ti.title,ti.sell_point,ti.num,barcode, ti.status, format(ti.price/1000,2)as priceF,  tic.name as category
FROM tb_item  ti LEFT JOIN tb_item_cat tic ON  ti.cid = tic.id


        ti.title like '%${condition.title}%'


        ti.barcode = #{condition.barcode}


order by ti.createDate desc

    limit ${offset}, ${rows}

select count(1) FROM tb_item ti LEFT JOIN tb_item_cat tic ON ti.cid = tic.id
ti.title like '%${condition.title}%'
ti.barcode = #{condition.barcode}

SELECT ti.* ,format(ti.price/1000,2)as priceF,tic.name as category
FROM tb_item  ti LEFT JOIN tb_item_cat tic ON  ti.cid = tic.id
WHERE ti.id = #{id}
