mapper.insert:
        INSERT INTO tb_user (
		    user_name
		    ,password
		    ,name
		    ,age
		    ,sex
		    ,birthday
		    ,created
		    ,updated
        ) VALUES (
		    	#{user_name}
		    	,#{password}
		    	,#{name}
		    	,#{age}
		    	,#{sex}
		    	,#{birthday}
		    	,#{created}
		    	,#{updated}
        )

        	SELECT LAST_INSERT_ID() AS ID

